import '../App.css'; 
import React, { Component } from "react";
import axios from "axios";
import { Table } from "reactstrap";


class Courses extends Component {

    state = {
        activeItem: {
          cid: "",
          ccode: "",
          cname: "",
          cdesc: "",
        },
        courseList: []
      };

      componentDidMount() {
        this.refreshList();
      }

      refreshList = () => {
        axios
          .get("/api/courses/")
          .then((res) => this.setState({ courseList: res.data }))
          .catch((err) => console.log(err));
      };

      renderItems = () => {
        const newItems = this.state.courseList;
        return ( (
          <Table>
            <thead>
                <th>ID</th>
                <th>Code</th>
                <th>Name</th>
                <th>Description</th>
                <input
                id="team_name"
                type="text"
                name="name_field"
                />
                <input type="submit" value="OK" />
            </thead>
            <tbody>
              {newItems.map((item) => {
                return(
                  <tr>
                    <td>{item.cid}</td>
                    <td>{item.ccode}</td>
                    <td>{item.cname}</td>
                    <td>{item.cdesc}</td>
                  </tr>
                );
              })}
            </tbody>
          </Table>
        ));
      };

      render() {
        return (
        <main className="content">
            {this.renderItems()}
        </main>
        )
      }
    }

  //closing statement
export default Courses;
