import '../App.css'; 
import React, { Component } from "react";
import axios from "axios";
import Modal from "../components/Modal";
import ModalDel from  "../components/ModalDel";
import { 
    Table,
    Button,
    Form,
    FormGroup,
    Input,
    Label
} from "reactstrap";


class Courses extends Component {

    state = {
        activeItem: {
          cid: "",
          ccode: "",
          cname: "",
          cdesc: "",
        },
        courseList: []
      };

      componentDidMount() {
        this.refreshList();
      }

      refreshList = () => {
        axios
          .get("/api/courses/")
          .then((res) => this.setState({ courseList: res.data }))
          .catch((err) => console.log(err));
      };

      toggle = () => {
        this.setState({ modal: !this.state.modal });
      };

      toggledel = () => {
        this.setState({ modaldel: !this.state.modaldel });
      };

      handleSubmit = (item) => {
        //toggle the window closed
        this.toggle();
        //check to see if the item ID already exists
        if (item.id) {
          //if it does, update the values in the database with the newly added values
          axios
            .put(`/api/students/${item.id}/`, item)
            //refresh the list
            .then((res) => this.refreshList());
          return;
        }
        //If the item ID does not already exist, create a brand new entry in the Database
        axios
          .post("/api/students/", item)
          .then((res) => this.refreshList());
      };


      renderItems = () => {
        const newItems = this.state.courseList;
        return ( (
          <Table>
            {newItems.map((item) => {
                return(
                  <tr>
                    <td>{item.cid}</td>
                    <td>{item.ccode}</td>
                    <td>{item.cname}</td>
                    <td>{item.cdesc}</td>
                  </tr>
                );
              })}
          </Table>
        ));
      };

      render() {
        return (
        <main className="content">
            {this.renderItems()}
        </main>
        )
      }
    }

  //closing statement
export default Courses;
