    const url = "http://127.0.0.1:8000/api/courses/";

    const [data, setData] = useState([]);

    const fetchInfo = () => {
        return axios.get(url).then((res) => setData(res.data));
      };


    useEffect(() => {
      fetchInfo();
    }, []);

    return (
      <div className="App">
        <center>
          {data.map((dataObj) => {
            return (
              <div
                style={{
                  width: "15em",
                  backgroundColor: "#35D841",
                  padding: 2,
                  borderRadius: 10,
                  marginBlock: 10,
                }}
              >
                <p style={{ fontSize: 20, color: 'white' }}>{dataObj.cname}, {dataObj.cid}</p>
              </div>
            );
          })}
        </center>
      </div>
    );
  }

