import axios from 'axios';
import React from 'react';
import '../App.css'; 
import { useNavigate } from 'react-router-dom';
import SearchBar from './Search-bar'; // Import the SearchBar component
import CourseReview from './CourseReviews';

const url = 'http://127.0.0.1:8000/api';

function SearchResults() {

    state = {
        activeItem: {
          cid: "",
          ccode: "",
          cname: "",
          cdesc: "",
        },
        courseList: []
      };

      componentDidMount(); {
        this.refreshList();
      }

      refreshList = () => {
        axios
          .get("/api/courses/")
          .then((res) => this.setState({ courseList: res.data }))
          .catch((err) => console.log(err));
      };

      toggle = () => {
        this.setState({ modal: !this.state.modal });
      };

      toggledel = () => {
        this.setState({ modaldel: !this.state.modaldel });
      };

      handleSubmit = (item) => {
        this.toggle();
        if (item.cid) {
          axios
            .put(`/api/courses/${item.cid}/`, item)
            .then((res) => this.refreshList());
          return;
        }
        axios
          .post("/api/courses/", item)
          .then((res) => this.refreshList());
      };
      handleDelete = (item) => {
        //toggle the delete window closed
        this.toggledel();
        axios
          .delete(`/api/courses/${item.cid}/`)
          .then((res) => this.refreshList());
      };

      createItem = () => {
        const item = {cid: "", ccode: "", cname: "", cdesc: "" };
        this.setState({ activeItem: item, modal: !this.state.modal });
      };

      editItem = (item) => {
        this.setState({ activeItem: item, modal: !this.state.modal });
      };

      deleteItem = () => {
        const item = {cid: "", ccode: "", cname: "", cdesc: "" };
        this.setState({ activeItem: item, modaldel: !this.state.modaldel });
      };

      renderItems = () => {
        const newItems = this.state.courseList;
        return ( (
          <Table>

            {newItems.map((item) => {
                return(
                  <tr>
                    <td>{item.cid}</td>
                    <td>{item.ccode}</td>
                    <td>{item.cname}</td>
                    <td>{item.cdesc}</td>
                    <td><button 
                        className="btn btn-secondary mr-2"
                        onClick={() => this.editItem(item)}>Edit
                      </button>
                  </td>
                  </tr>
                );
              })}
          </Table>
        ));
      };




    return( 

    <div className='Search_results'> 
        <header className=' black-bar'>
        <h1> Search_results page</h1>
            <div className='top-right-container'>
            <button className="sign-in-button" onClick={handleSignInClick}>Sign-In</button>
            <button className="login-button" onClick={handleLoginClick}>Login</button>
    </div>
    </header>
        <div className='search-bar-container'>
            <SearchBar/>
        </div>

        <div className='course-reviews-container'>
        {courseReviews.map((review, index) => (
        // Render the CourseReview component for each review
        <CourseReview key={index} review={review} />
        ))}
        </div>
        <div className="bottom-black-bar">
                <p>Footer content here</p>
        </div>

    {/* Add more content here */}
    </div>
    );

}

export default SearchResults;
