import axios from 'axios';
import React, { useState, useEffect } from "react";
import { Table } from "reactstrap";
import '../App.css'; 
import { useNavigate } from 'react-router-dom';
import SearchBar from './Search-bar'; // Import the SearchBar component
import CourseReview from './CourseReviews';

const url = 'http://127.0.0.1:8000/api';


function SearchResults() {
    const url = "http://127.0.0.1:8000/api/courses/";

    const [data, setData] = useState([]);

    const fetchInfo = () => {
        return axios.get(url).then((res) => setData(res.data));
      };


    useEffect(() => {
      fetchInfo();
    }, []);

    const navigate = useNavigate();
    const handleSignInClick = () => {
        navigate('/SignIn'); // Navigate to the sign-in page
    };
    const handleLoginClick = () => {
        navigate('/Login'); // Navigate to the sign-in page
    };




    return( 

    <div className='Search_results'> 
        <header className=' black-bar'>
        <h1>Search Results</h1>
            <div className='top-right-container'>
            <button className="sign-in-button" onClick={handleSignInClick}>Sign-In</button>
            <button className="login-button" onClick={handleLoginClick}>Login</button>
    </div>
    </header>
        <div className='search-bar-container'>
            <SearchBar/>
        </div>

        <div className='course-reviews-container'>
        <center>
            <table align = "center">
          {data.map((dataObj) => {
            return (
              <div
                style={{
                  width: "500px",
                  backgroundColor: "#000000",
                  padding: 2,
                  borderRadius: 10,
                  marginBlock: 10,
                }}
              >
                <p style={{ fontSize: 20, color: 'white' }}>{dataObj.cid} {dataObj.ccode}</p>
                <p style={{ fontSize: 20, color: 'white' }}>{dataObj.cname}</p>
                <p style={{ fontSize: 20, color: 'white' }}>{dataObj.cdesc}</p>
              </div>
            );
          })}
          </table>
        </center>
      </div>
        <div className="bottom-black-bar">
                <p>Footer content here</p>
        </div>

    {/* Add more content here */}
    </div>
    );

}

export default SearchResults;
