import '../App.css'; 
import React, { Component } from "react";
import axios from "axios";
import { 
    Table,
    Button,
    Form,
    FormGroup,
    Input,
    Label
} from "reactstrap";


class Courses extends Component {

    state = {
        activeItem: {
          cid: "",
          ccode: "",
          cname: "",
          cdesc: "",
        },
        courseList: []
      };

      componentDidMount() {
        this.refreshList();
      }

      refreshList = () => {
        axios
          .get("/api/courses/")
          .then((res) => this.setState({ courseList: res.data }))
          .catch((err) => console.log(err));
      };

      renderItems = () => {
        const newItems = this.state.courseList;
        return ( (
          <Table>
            {newItems.map((item) => {
                return(
                  <tr>
                    <td>{item.cid}</td>
                    <td>{item.ccode}</td>
                    <td>{item.cname}</td>
                    <td>{item.cdesc}</td>
                  </tr>
                );
              })}
          </Table>
        ));
      };

      render() {
        return (
        <main className="content">
            {this.renderItems()}
        </main>
        )
      }
    }

  //closing statement
export default Courses;
